mapper.update:
UPDATE seckill set number = number -1
where seckill_id = #{seckillId}
AND  start_time  <=  #{killTime}
AND  end_time >= #{killTime}
AND number >0;

mapper.select:
SELECT seckill_id seckillId,name,number,start_time startTime,end_time endTime,create_time createTime
FROM seckill
WHERE seckill_id = #{seckillId}
